                  chart={tasks}
                />
              </MDBox>
            </Grid>
          </Grid>
        </MDBox> */}
        <MDBox>
          <Grid container spacing={3}>
            <Grid item xs={6} md={6} lg={6}>
              {showApp ? (
                <Card style={{ backgroundColor: "#318CE7", maxHeight: 350 }}>
                  <MDBox
                    variant="gradient"
                    bgColor="white"
                    borderRadius="lg"
                    coloredShadow="success"
                    mt={2}
                    mx={0}
                    p={1}
                    textAlign="left"
                  >
                    <MDTypography
                      variant="h4"
                      fontWeight="medium"
                      color="info"
                      textAlign="center"
                      mt={1}
                    >
                      Appraisals
                    </MDTypography>
                  </MDBox>
                  &nbsp;
                  <div
                    className="scrollbar scrollbar-primary mt-2 mx-auto"
                    style={scrollContainerStyle}
                  >
                    <Container>
                      <div className="row">
                        {allApp.map((item) => (
                          <Grid item xs={12} md={12} lg={12} key={item.id}>
                            <Card sx={{ maxWidth: 345 }}>
                              <CardContent>
                                <MDTypography
                                  variant="h5"
                                  fontWeight="medium"
                                  fontSize="120%"
                                  color="info"
                                  textAlign="left"
                                  mt={1}
                                >
                                  {item.name}
                                </MDTypography>
                                <MDTypography
                                  variant="h6"
                                  color="text"
                                  fontSize="75%"
                                  textAlign="left"
                                  mt={1}
                                >
                                  You have been selected for this Appraisal
                                </MDTypography>
                                <MDTypography
                                  variant="h6"
                                  color="text"
                                  fontSize="75%"
                                  textAlign="left"
                                  mt={1}
                                >
                                  Appraisee - {item.appraiseeName}
                                </MDTypography>
                                <MDTypography
                                  variant="h6"
                                  color="text"
                                  fontSize="75%"
                                  textAlign="left"
                                  mt={0}
                                >
                                  Created By - {item.createdByName}
                                </MDTypography>
                              </CardContent>
                              <CardActions>
                                <div align="right">
                                  <MDButton
                                    variant="gradient"
                                    color="info"
                                    onClick={() => handleAppraise(item.id)}
                                    width="50%"
                                  >
                                    Appraise
                                  </MDButton>
                                </div>
                              </CardActions>
                            </Card>
                            &nbsp;
                          </Grid>
                        ))}
                      </div>
                    </Container>
                  </div>
                  &nbsp;
                </Card>
              ) : (
                <Card style={{ backgroundColor: "#318CE7", maxHeight: 350 }}>
                  {" "}
                  <MDTypography
                    variant="h3"
                    fontWeight="bold"
                    color="white"
                    textAlign="center"
                    mt={1}
                  >
                    No Appraisal At The Moment
                  </MDTypography>
                  <Icon
                    fontSize="medium"
                    sx={{ fontSize: 100, alignSelf: "center" }}
                    color="disabled"
                  >
                    sentiment_dissatisfied
                  </Icon>
                </Card>
              )}
            </Grid>
            <Grid item xs={6} md={6} lg={6}>
              {showPolls ? (
                <Card sx={{ maxHeight: 350 }}>
                  <div
                    className="scrollbar scrollbar-primary mt-2 mx-auto"
                    style={scrollContainerStyle}
                  >
                    <MDBox mb={1.5}>
                      <Container>
                        <div className="row">
                          {polls.map((api) => (
                            <Grid container spacing={0} key={api.id}>
                              <Grid item xs={12} md={12} lg={12}>
                                <Link to={`/polls/vote-Polls?id=${api.id}`}>
                                  <Card style={{ backgroundColor: "#318CE7" }}>
                                    <CardContent>
                                      <MDTypography
                                        variant="h4"
                                        fontWeight="medium"
                                        color="white"
                                        textAlign="left"
                                        mt={1}
                                      >
                                        Poll
                                      </MDTypography>
                                      <div style={{ color: "#f5f5f5" }}>{api.question}</div>
                                    </CardContent>
                                  </Card>{" "}
                                  &nbsp; &nbsp;
                                </Link>
                              </Grid>
                            </Grid>
                          ))}
                        </div>
                      </Container>
                    </MDBox>
                  </div>
                </Card>
              ) : (
                <Card style={{ backgroundColor: "#318CE7", maxHeight: 350 }}>
                  {" "}
                  <MDTypography
                    variant="h3"
                    fontWeight="bold"
                    color="white"
                    textAlign="center"
                    mt={1}
                  >
                    No Poll At The Moment
                  </MDTypography>
                  <Icon
                    fontSize="medium"
                    sx={{ fontSize: 100, alignSelf: "center" }}
                    color="disabled"
                  >
                    sentiment_dissatisfied
                  </Icon>
                </Card>
              )}
            </Grid>
          </Grid>
        </MDBox>
      </MDBox>
      <Backdrop sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }} open={opened}>
        <CircularProgress color="info" />
      </Backdrop>
      <Footer />
    </DashboardLayout>
  );
}

export default Dashboard;
