/* eslint-disable react/prop-types */

// @mui material components

// Soft UI Dashboard React components
import { useEffect, useState } from "react";
import { Dropdown } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import Icon from "@mui/material/Icon";
import Swal from "sweetalert2";
import withReactContent from "sweetalert2-react-content";
import PHeaders from "postHeader";
import GHeaders from "getHeader";
import { useNavigate } from "react-router-dom";

export default function TimeOffRequestData() {
  const MySwal = withReactContent(Swal);
  const [items, setItems] = useState([]);

  const navigate = useNavigate();

  const { allPHeaders: myHeaders } = PHeaders();
  const { allGHeaders: miHeaders } = GHeaders();

  // timeofftypedetails
  // Method to handle diable
  const handleUpdate = (
    idx,
    empSetupIdx,
    daysx,
    daysapprovex,
    startx,
    endx,
    resumex,
    dutyrelieverx,
    createdx,
    purposex,
    deletex,
    approvex,
    adminx,
    reasonx,
    statusx,
    empIDx
  ) => {
    const data11 = JSON.parse(localStorage.getItem("user1"));
    const orgIDs = data11.orgID;
    const personalIds = data11.personalID;

    if (approvex !== personalIds) {
      MySwal.fire({
        title: "PROCESS_DENIED",
        type: "success",
        text: "You Are Not Permitted To Approve This Request",
      }).then(() => {
        window.location.reload();
      });
    } else if (reasonx !== null && reasonx !== "") {
      MySwal.fire({
        title: "PROCESS_DENIED",
        type: "success",
        text: "Decision Already Made For This Request",
      }).then(() => {
        window.location.reload();
      });
    } else {
      const raw = JSON.stringify({
        id: idx,
        orgID: orgIDs,
        empID: empIDx,
        empSetupID: empSetupIdx,
        noOfDaysRequested: daysx,
        noOfDaysApproved: daysapprovex,
        startDate: startx,
        endDate: endx,
        resumptionDate: resumex,
        dutyRelieverID: dutyrelieverx,
        createdDate: createdx,
        purpose: purposex,
        deleteFlag: deletex,
        approverID: approvex,
        adminID: adminx,
        reasonForDisapproval: reasonx,
        status: statusx,
      });
      console.log(raw);
      const requestOptions = {
        method: "POST",
        headers: myHeaders,
        body: raw,
        redirect: "follow",
      };

      fetch(
        `${process.env.REACT_APP_NSUTANA_URL}/employeetimeofftransaction/update`,
        requestOptions
      )
        .then(async (res) => {
          const aToken = res.headers.get("token-1");
          localStorage.setItem("rexxdex", aToken);
          return res.json();
        })
        .then((result) => {
          if (result.message === "Expired Access") {
            navigate("/authentication/sign-in");
            window.location.reload();
          }
          if (result.message === "Token Does Not Exist") {
            navigate("/authentication/sign-in");
            window.location.reload();
          }
          if (result.message === "Unauthorized Access") {
            navigate("/authentication/forbiddenPage");
            window.location.reload();
          }
          MySwal.fire({
            title: result.status,
            type: "success",
            text: result.message,
          }).then(() => {
            window.location.reload();
          });
        })
        .catch((error) => {
          MySwal.fire({
            title: error.status,
            type: "error",
            text: error.message,
          });
        });
    }
  };

  // const getCurrentDate = () => new Date().getTime();

  // const changeUpdateDate = (timestamp) => {
  //   const date = new Date(timestamp);
  //   let month = "0";
  //   if (date.getMonth() + 1 < 10) {
  //     const mymonth = date.getMonth() + 1;
